import { Pool, neonConfig } from '@neondatabase/serverless';
import { drizzle } from 'drizzle-orm/neon-serverless';
import ws from "ws";
import * as schema from "@shared/schema";
import session from 'express-session';
import connectPg from 'connect-pg-simple';

neonConfig.webSocketConstructor = ws;

if (!process.env.DATABASE_URL) {
  throw new Error(
    "DATABASE_URL must be set. Did you forget to provision a database?",
  );
}

export const pool = new Pool({ connectionString: process.env.DATABASE_URL });
export const db = drizzle({ client: pool, schema });

// Create session store
const PostgresSessionStore = connectPg(session);
export const sessionStore = new PostgresSessionStore({ 
  pool, 
  createTableIfMissing: false // Don't recreate if exists
});

server/emailService.ts.content:
import nodemailer from 'nodemailer';

interface EmailConfig {
  host: string;
  port: number;
  secure: boolean;
  auth: {
    user: string;
    pass: string;
  };
}

class EmailService {
  private transporter: nodemailer.Transporter;

  constructor() {
    // Use environment variables for email configuration
    const emailConfig: EmailConfig = {
      host: process.env.SMTP_HOST || 'smtp.gmail.com',
      port: parseInt(process.env.SMTP_PORT || '587'),
      secure: process.env.SMTP_SECURE === 'true',
      auth: {
        user: process.env.SMTP_USER || process.env.EMAIL_USER || '',
        pass: process.env.SMTP_PASS || process.env.EMAIL_PASS || '',
      },
    };

    this.transporter = nodemailer.createTransport(emailConfig);
  }

  async sendOtpEmail(email: string, otp: string, firstName?: string): Promise<boolean> {
    try {
      const mailOptions = {
        from: process.env.FROM_EMAIL || 'noreply@uniconnect.com',
        to: email,
        subject: 'UniConnect - Password Reset OTP',
        html: `
          <div style="font-family: Arial, sans-serif; max-width: 600px; margin: 0 auto;">
            <h2 style="color: #000; text-align: center;">UniConnect</h2>
            <div style="background-color: #f8f9fa; padding: 20px; border-radius: 8px;">
              <h3>Password Reset Request</h3>
              ${firstName ? `<p>Hi ${firstName},</p>` : '<p>Hi,</p>'}
              <p>You requested to reset your password. Use the following OTP to proceed:</p>
              <div style="text-align: center; margin: 20px 0;">
                <span style="background-color: #000; color: #fff; padding: 15px 30px; font-size: 24px; font-weight: bold; border-radius: 8px; letter-spacing: 5px;">${otp}</span>
              </div>
              <p>This OTP will expire in 10 minutes.</p>
              <p>If you didn't request this password reset, please ignore this email.</p>
            </div>
            <p style="text-align: center; color: #666; font-size: 12px; margin-top: 20px;">
              This email was sent by UniConnect - University Social Platform
            </p>
          </div>
        `,
      };

      await this.transporter.sendMail(mailOptions);
      return true;
    } catch (error) {
      console.error('Error sending OTP email:', error);
      return false;
    }
  }

  async sendPasswordChangeNotification(email: string, firstName?: string): Promise<boolean> {
    try {
      const mailOptions = {
        from: process.env.FROM_EMAIL || 'noreply@uniconnect.com',
        to: email,
        subject: 'UniConnect - Password Changed Successfully',
        html: `
          <div style="font-family: Arial, sans-serif; max-width: 600px; margin: 0 auto;">
            <h2 style="color: #000; text-align: center;">UniConnect</h2>
            <div style="background-color: #f8f9fa; padding: 20px; border-radius: 8px;">
              <h3>Password Changed Successfully</h3>
              ${firstName ? `<p>Hi ${firstName},</p>` : '<p>Hi,</p>'}
              <p>Your password has been successfully changed.</p>
              <p>If you didn't make this change, please contact our support team immediately.</p>
              <div style="text-align: center; margin: 20px 0;">
                <a href="${process.env.APP_URL || 'https://uniconnect.com'}" style="background-color: #000; color: #fff; padding: 12px 24px; text-decoration: none; border-radius: 8px;">Sign In to Your Account</a>
              </div>
            </div>
            <p style="text-align: center; color: #666; font-size: 12px; margin-top: 20px;">
              This email was sent by UniConnect - University Social Platform
            </p>
          </div>
        `,
      };

      await this.transporter.sendMail(mailOptions);
      return true;
    } catch (error) {
      console.error('Error sending password change notification:', error);
      return false;
    }
  }

  async sendPasswordEmail(email: string, password: string, firstName?: string): Promise<boolean> {
    try {
      const mailOptions = {
        from: process.env.FROM_EMAIL || 'noreply@entryfox.com',
        to: email,
        subject: 'Welcome to EntryFox - Your Account Password',
        html: `
          <div style="font-family: Arial, sans-serif; max-width: 600px; margin: 0 auto;">
            <h2 style="color: #000; text-align: center;">EntryFox</h2>
            <div style="background-color: #f8f9fa; padding: 20px; border-radius: 8px;">
              <h3>Welcome to EntryFox!</h3>
              ${firstName ? `<p>Hi ${firstName},</p>` : '<p>Hi,</p>'}
              <p>Your account has been created successfully. Here are your login credentials:</p>
              <div style="background-color: #e9ecef; padding: 15px; border-radius: 5px; margin: 15px 0;">
                <p><strong>Email:</strong> ${email}</p>
                <p><strong>Password:</strong> <span style="background-color: #000; color: #fff; padding: 5px 10px; border-radius: 3px; font-family: monospace;">${password}</span></p>
              </div>
              <p>Please keep this password safe and consider changing it after your first login.</p>
              <p>You can now login to your EntryFox account and start connecting with vendors and customers!</p>
            </div>
            <p style="text-align: center; color: #666; font-size: 12px; margin-top: 20px;">
              This email was sent by EntryFox - Your Community Marketplace
            </p>
          </div>
        `,
      };

      await this.transporter.sendMail(mailOptions);
      return true;
    } catch (error) {
      console.error('Error sending password email:', error);
      return false;
    }
  }

  async sendWelcomeEmail(email: string, firstName?: string): Promise<boolean> {
    try {
      const mailOptions = {
        from: process.env.FROM_EMAIL || 'noreply@entryfox.com',
        to: email,
        subject: 'Welcome to EntryFox!',
        html: `
          <div style="font-family: Arial, sans-serif; max-width: 600px; margin: 0 auto;">
            <h2 style="color: #000; text-align: center;">Welcome to UniConnect!</h2>
            <div style="background-color: #f8f9fa; padding: 20px; border-radius: 8px;">
              ${firstName ? `<p>Hi ${firstName},</p>` : '<p>Hi,</p>'}
              <p>Welcome to UniConnect - your university's social platform!</p>
              <p>You can now:</p>
              <ul>
                <li>Connect with fellow students and vendors</li>
                <li>Share updates and experiences</li>
                <li>Discover what's happening on campus</li>
                <li>Access the marketplace (coming soon!)</li>
              </ul>
              <div style="text-align: center; margin: 20px 0;">
                <a href="${process.env.APP_URL || 'https://uniconnect.com'}" style="background-color: #000; color: #fff; padding: 12px 24px; text-decoration: none; border-radius: 8px;">Get Started</a>
              </div>
            </div>
            <p style="text-align: center; color: #666; font-size: 12px; margin-top: 20px;">
              This email was sent by UniConnect - University Social Platform
            </p>
          </div>
        `,
      };

      await this.transporter.sendMail(mailOptions);
      return true;
    } catch (error) {
      console.error('Error sending welcome email:', error);
      return false;
    }
  }
}

export const emailService = new EmailService();

