import { Switch, Route } from "wouter";
import { queryClient } from "./lib/queryClient";
import { QueryClientProvider } from "@tanstack/react-query";
import { Toaster } from "@/components/ui/toaster";
import { TooltipProvider } from "@/components/ui/tooltip";
import { useAuth, AuthProvider } from "@/hooks/useAuth";
import NotFound from "@/pages/not-found";
import Landing from "@/pages/landing";
import Welcome from "@/pages/welcome";
import UserSelection from "@/pages/user-selection";
import Register from "@/pages/register";
import Login from "@/pages/login";
import ForgotPassword from "@/pages/forgot-password";
import VerifyOtp from "@/pages/verify-otp";
import ResetPassword from "@/pages/reset-password";
import UploadPicture from "@/pages/upload-picture";
import Success from "@/pages/success";
import Social from "@/pages/social";
import VendorDashboard from "@/pages/vendor-dashboard";
import CustomerDashboard from "@/pages/customer-dashboard";

function Router() {
  const { user, isLoading } = useAuth();

  return (
    <Switch>
      {isLoading || !user ? (
        <>
          <Route path="/" component={Login} />
          <Route path="/welcome" component={Welcome} />
          <Route path="/user-selection" component={UserSelection} />
          <Route path="/register" component={Register} />
          <Route path="/forgot-password" component={ForgotPassword} />
          <Route path="/verify-otp" component={VerifyOtp} />
          <Route path="/reset-password" component={ResetPassword} />
          <Route path="/upload-picture" component={UploadPicture} />
          <Route path="/success" component={Success} />
        </>
      ) : (
        <>
          <Route path="/" component={Social} />
          <Route path="/vendor-dashboard" component={VendorDashboard} />
          <Route path="/customer-dashboard" component={CustomerDashboard} />
          <Route path="/social" component={Social} />
        </>
      )}
      <Route component={NotFound} />
    </Switch>
  );
}

function App() {
  return (
    <QueryClientProvider client={queryClient}>
      <AuthProvider>
        <TooltipProvider>
          <Toaster />
          <Router />
        </TooltipProvider>
      </AuthProvider>
    </QueryClientProvider>
  );
}

export default App;

