import { z } from "zod";
import { Form, FormControl, FormField, FormItem, FormMessage } from "@/components/ui/form";
import { useMutation } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { useLocation } from "wouter";

const forgotPasswordSchema = z.object({
  email: z.string().email("Please enter a valid email"),
});

type ForgotPasswordForm = z.infer<typeof forgotPasswordSchema>;

export default function ForgotPassword() {
  const { toast } = useToast();
  const [, setLocation] = useLocation();

  const form = useForm<ForgotPasswordForm>({
    resolver: zodResolver(forgotPasswordSchema),
    defaultValues: {
      email: "",
    },
  });

  const forgotPasswordMutation = useMutation({
    mutationFn: async (data: ForgotPasswordForm) => {
      return await apiRequest("POST", "/api/forgot-password", data);
    },
    onSuccess: () => {
      toast({
        title: "Email Sent",
        description: "Check your email for password reset instructions.",
      });
      setLocation("/verify-otp");
    },
    onError: (error) => {
      toast({
        title: "Error",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const onSubmit = (data: ForgotPasswordForm) => {
    forgotPasswordMutation.mutate(data);
  };

  return (
    <div className="min-h-screen bg-white flex items-center justify-center p-4">
      <div className="w-full max-w-sm relative">
        {/* Close Button */}
        <Button
          variant="ghost"
          size="icon"
          className="absolute -top-2 -right-2 w-10 h-10 rounded-full bg-black text-white hover:bg-gray-800"
          asChild
        >
          <Link href="/login">
            <X className="h-5 w-5" />
          </Link>
        </Button>

        {/* Header */}
        <div className="text-left mb-8">
          <h1 className="text-2xl font-bold mb-4">Reset password</h1>
          <p className="text-gray-600 text-sm leading-relaxed">
            Enter the email associated with your account and we will send you an email with instructions to reset your password
          </p>
        </div>

        {/* Form */}
        <Form {...form}>
          <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-6">
            <FormField
              control={form.control}
              name="email"
              render={({ field }) => (
                <FormItem>
                  <FormControl>
                    <div className="relative">
                      <Mail className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-500 h-5 w-5" />
                      <Input 
                        placeholder="Email" 
                        className="pl-12 h-14 rounded-2xl border-gray-300 bg-gray-50"
                        {...field} 
                      />
                    </div>
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <Button 
              type="submit" 
              disabled={forgotPasswordMutation.isPending}
              className="w-full h-14 bg-black text-white hover:bg-gray-800 rounded-full font-medium"
            >
              {forgotPasswordMutation.isPending ? "Sending..." : "Send instructions"}
            </Button>
          </form>
        </Form>
      </div>
    </div>
  );
}

