import { useState } from "react";
import { useLocation } from "wouter";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { X, Mail, Lock, Eye, EyeOff, User } from "lucide-react";
import { Link } from "wouter";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { Form, FormControl, FormField, FormItem, FormMessage } from "@/components/ui/form";
import { useMutation } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { FoxLogo } from "@/components/FoxLogo";

const registerSchema = z.object({
  name: z.string().min(1, "Name is required"),
  email: z.string().email("Invalid email address"),
  password: z.string().min(6, "Password must be at least 6 characters"),
  confirmPassword: z.string(),
  userType: z.enum(["vendor", "customer"]),
}).refine((data) => data.password === data.confirmPassword, {
  message: "Passwords don't match",
  path: ["confirmPassword"],
});

type RegisterForm = z.infer<typeof registerSchema>;

export default function Register() {
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
  const userType = new URLSearchParams(window.location.search).get('type') || 'customer';

  const form = useForm<RegisterForm>({
    resolver: zodResolver(registerSchema),
    defaultValues: {
      name: "",
      email: "",
      password: "",
      confirmPassword: "",
      userType: userType as "vendor" | "customer",
    },
  });

  const registerMutation = useMutation({
    mutationFn: async (data: RegisterForm) => {
      return await apiRequest("POST", "/api/register", data);
    },
    onSuccess: async (response) => {
      const result = await response.json();
      localStorage.setItem('tempUserId', result.userId);
      localStorage.setItem('tempUserType', userType);
      setLocation("/user-selection");
    },
    onError: (error) => {
      toast({
        title: "Registration Failed",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const onSubmit = (data: RegisterForm) => {
    registerMutation.mutate(data);
  };

  return (
    <div className="min-h-screen bg-white flex items-center justify-center p-4">
      <div className="w-full max-w-sm relative">
        {/* Close Button */}
        <Button
          variant="ghost"
          size="icon"
          className="absolute -top-2 -right-2 w-10 h-10 rounded-full bg-black text-white hover:bg-gray-800"
          asChild
        >
          <Link href="/user-selection">
            <X className="h-5 w-5" />
          </Link>
        </Button>

        {/* Header */}
        <div className="text-center mb-8">
          <h1 className="text-2xl font-bold mb-6">Create your Account</h1>
          <FoxLogo size={80} className="mb-8" />
        </div>

        {/* Registration Form */}
        <Form {...form}>
          <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
            <FormField
              control={form.control}
              name="name"
              render={({ field }) => (
                <FormItem>
                  <FormControl>
                    <div className="relative">
                      <User className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-500 h-5 w-5" />
                      <Input 
                        placeholder="Name" 
                        className="pl-12 h-14 rounded-2xl border-gray-300 bg-gray-50"
                        {...field} 
                      />
                    </div>
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="email"
              render={({ field }) => (
                <FormItem>
                  <FormControl>
                    <div className="relative">
                      <Mail className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-500 h-5 w-5" />
                      <Input 
                        placeholder="Email" 
                        className="pl-12 h-14 rounded-2xl border-gray-300 bg-gray-50"
                        {...field} 
                      />
                    </div>
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="password"
              render={({ field }) => (
                <FormItem>
                  <FormControl>
                    <div className="relative">
                      <Lock className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-500 h-5 w-5" />
                      <Input 
                        type={showPassword ? "text" : "password"}
                        placeholder="Password" 
                        className="pl-12 pr-12 h-14 rounded-2xl border-gray-300 bg-gray-50"
                        {...field} 
                      />
                      <Button
                        type="button"
                        variant="ghost"
                        size="icon"
                        className="absolute right-3 top-1/2 transform -translate-y-1/2 h-5 w-5 p-0"
                        onClick={() => setShowPassword(!showPassword)}
                      >
                        {showPassword ? (
                          <EyeOff className="h-4 w-4 text-gray-500" />
                        ) : (
                          <Eye className="h-4 w-4 text-gray-500" />
                        )}
                      </Button>
                    </div>
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="confirmPassword"
              render={({ field }) => (
                <FormItem>
                  <FormControl>
                    <div className="relative">
                      <Lock className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-500 h-5 w-5" />
                      <Input 
                        type={showConfirmPassword ? "text" : "password"}
                        placeholder="Confirm password" 
                        className="pl-12 pr-12 h-14 rounded-2xl border-gray-300 bg-gray-50"
                        {...field} 
                      />
                      <Button
                        type="button"
                        variant="ghost"
                        size="icon"
                        className="absolute right-3 top-1/2 transform -translate-y-1/2 h-5 w-5 p-0"
                        onClick={() => setShowConfirmPassword(!showConfirmPassword)}
                      >
                        {showConfirmPassword ? (
                          <EyeOff className="h-4 w-4 text-gray-500" />
                        ) : (
                          <Eye className="h-4 w-4 text-gray-500" />
                        )}
                      </Button>
                    </div>
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <Button 
              type="submit" 
              disabled={registerMutation.isPending}
              className="w-full h-14 bg-black text-white hover:bg-gray-800 rounded-full font-medium"
            >
              {registerMutation.isPending ? "Creating..." : "Sign up"}
            </Button>
          </form>
        </Form>

        {/* Footer */}
        <div className="text-center mt-6">
          <p className="text-gray-500">
            Already have an account?{" "}
            <Link href="/login">
              <Button variant="link" className="text-black font-medium p-0">
                Sign in
              </Button>
            </Link>
          </p>
        </div>
      </div>
    </div>
  );
}

client/src/pages/reset-password.tsx.content:
import { useLocation } from "wouter";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { useMutation } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";

const resetPasswordSchema = z.object({
  password: z.string().min(6, "Password must be at least 6 characters"),
  confirmPassword: z.string(),
}).refine((data) => data.password === data.confirmPassword, {
  message: "Passwords don't match",
  path: ["confirmPassword"],
});

type ResetPasswordForm = z.infer<typeof resetPasswordSchema>;

export default function ResetPassword() {
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const email = localStorage.getItem('resetEmail') || '';

  const form = useForm<ResetPasswordForm>({
    resolver: zodResolver(resetPasswordSchema),
    defaultValues: {
      password: "",
      confirmPassword: "",
    },
  });

  const resetPasswordMutation = useMutation({
    mutationFn: async (data: ResetPasswordForm) => {
      // We need to get the OTP from somewhere - for now, we'll assume it's valid
      const otp = localStorage.getItem('verifiedOtp') || '123456';
      return await apiRequest("POST", "/api/reset-password", {
        email,
        otp,
        password: data.password,
        confirmPassword: data.confirmPassword,
      });
    },
    onSuccess: () => {
      localStorage.removeItem('resetEmail');
      localStorage.removeItem('verifiedOtp');
      setLocation("/login");
      toast({
        title: "Password Reset Successful",
        description: "Your password has been reset. You can now sign in.",
      });
    },
    onError: (error) => {
      toast({
        title: "Reset Failed",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const onSubmit = (data: ResetPasswordForm) => {
    resetPasswordMutation.mutate(data);
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-background p-4">
      <div className="w-full max-w-md">
        {/* Header */}
        <div className="text-center mb-8">
          <h1 className="text-2xl font-bold mb-2">Create New Password</h1>
          <p className="text-muted-foreground">Your new password must be different from previous used passwords</p>
        </div>

        {/* Reset Password Form */}
        <Card>
          <CardContent className="pt-6">
            <Form {...form}>
              <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
                <FormField
                  control={form.control}
                  name="password"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>New Password</FormLabel>
                      <FormControl>
                        <Input type="password" placeholder="New Password" {...field} />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="confirmPassword"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Confirm New Password</FormLabel>
                      <FormControl>
                        <Input type="password" placeholder="Confirm New Password" {...field} />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <Button 
                  type="submit" 
                  className="w-full bg-black text-white hover:bg-gray-800"
                  disabled={resetPasswordMutation.isPending}
                >
                  {resetPasswordMutation.isPending ? "Updating Password..." : "Update Password"}
                </Button>
              </form>
            </Form>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

client/src/pages/social.tsx.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardHeader } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { Textarea } from "@/components/ui/textarea";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { Heart, MessageCircle, Share, MoreHorizontal, Image, Send } from "lucide-react";
import { FoxLogo } from "@/components/FoxLogo";

interface Post {
  id: number;
  content: string;
  imageUrl?: string;
  likesCount: number;
  commentsCount: number;
  createdAt: string;
  user: {
    id: number;
    firstName: string;
    lastName: string;
    email: string;
    profileImageUrl?: string;
  };
  isLiked?: boolean;
}

interface Comment {
  id: number;
  content: string;
  createdAt: string;
  user: {
    id: number;
    firstName: string;
    lastName: string;
    profileImageUrl?: string;
  };
}

export default function Social() {
  const { user } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [newPost, setNewPost] = useState("");
  const [showComments, setShowComments] = useState<number | null>(null);
  const [newComment, setNewComment] = useState("");

  // Fetch posts
  const { data: posts = [], isLoading } = useQuery({
    queryKey: ['/api/posts'],
    queryFn: async () => {
      const response = await fetch('/api/posts', {
        credentials: 'include',
      });
      if (!response.ok) throw new Error('Failed to fetch posts');
      return response.json();
    },
  });

  // Fetch comments for a post
  const { data: comments = [] } = useQuery({
    queryKey: ['/api/comments', showComments],
    queryFn: async () => {
      if (!showComments) return [];
      const response = await fetch(`/api/posts/${showComments}/comments`, {
        credentials: 'include',
      });
      if (!response.ok) throw new Error('Failed to fetch comments');
      return response.json();
    },
    enabled: !!showComments,
  });

  // Create post mutation
  const createPostMutation = useMutation({
    mutationFn: async (content: string) => {
      const response = await fetch('/api/posts', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        credentials: 'include',
        body: JSON.stringify({ content }),
      });
      if (!response.ok) throw new Error('Failed to create post');
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/posts'] });
      setNewPost("");
      toast({ title: "Post created successfully!" });
    },
  });

  // Like post mutation
  const likeMutation = useMutation({
    mutationFn: async ({ postId, isLiked }: { postId: number; isLiked: boolean }) => {
      const response = await fetch(`/api/posts/${postId}/${isLiked ? 'unlike' : 'like'}`, {
        method: 'POST',
        credentials: 'include',
      });
      if (!response.ok) throw new Error('Failed to update like');
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/posts'] });
    },
  });

  // Comment mutation
  const commentMutation = useMutation({
    mutationFn: async ({ postId, content }: { postId: number; content: string }) => {
      const response = await fetch(`/api/posts/${postId}/comments`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        credentials: 'include',
        body: JSON.stringify({ content }),
      });
      if (!response.ok) throw new Error('Failed to create comment');
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/posts'] });
      queryClient.invalidateQueries({ queryKey: ['/api/comments', showComments] });
      setNewComment("");
      toast({ title: "Comment added!" });
    },
  });

  const handleCreatePost = () => {
    if (newPost.trim()) {
      createPostMutation.mutate(newPost);
    }
  };

  const handleLike = (post: Post) => {
    likeMutation.mutate({ postId: post.id, isLiked: !!post.isLiked });
  };

  const handleComment = (postId: number) => {
    if (newComment.trim()) {
      commentMutation.mutate({ postId, content: newComment });
    }
  };

  const getUserDisplayName = (user: any) => {
    return user.firstName && user.lastName 
      ? `${user.firstName} ${user.lastName}`
      : user.email.split('@')[0];
  };

  if (isLoading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-center">
          <FoxLogo size={60} className="mx-auto mb-4 animate-pulse" />
          <p>Loading your feed...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <div className="bg-white border-b sticky top-0 z-10">
        <div className="max-w-2xl mx-auto px-4 py-3 flex items-center justify-between">
          <div className="flex items-center space-x-3">
            <FoxLogo size={32} />
            <h1 className="text-xl font-bold">EntryFox</h1>
          </div>
          <Avatar className="w-8 h-8">
            <AvatarImage src={user?.profileImageUrl} />
            <AvatarFallback>
              {user?.firstName?.[0]}{user?.lastName?.[0]}
            </AvatarFallback>
          </Avatar>
        </div>
      </div>

      <div className="max-w-2xl mx-auto px-4 py-6 space-y-6">
        {/* Create Post */}
        <Card>
          <CardContent className="pt-6">
            <div className="flex space-x-3">
              <Avatar>
                <AvatarImage src={user?.profileImageUrl} />
                <AvatarFallback>
                  {user?.firstName?.[0]}{user?.lastName?.[0]}
                </AvatarFallback>
              </Avatar>
              <div className="flex-1">
                <Textarea
                  placeholder="What's happening?"
                  value={newPost}
                  onChange={(e) => setNewPost(e.target.value)}
                  className="min-h-[100px] border-none resize-none focus:ring-0 text-lg placeholder:text-gray-500"
                />
                <div className="flex items-center justify-between mt-3">
                  <Button variant="ghost" size="sm">
                    <Image className="w-4 h-4 mr-2" />
                    Photo
                  </Button>
                  <Button 
                    onClick={handleCreatePost}
                    disabled={!newPost.trim() || createPostMutation.isPending}
                    className="rounded-full"
                  >
                    {createPostMutation.isPending ? "Posting..." : "Post"}
                  </Button>
                </div>
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Posts Feed */}
        {posts.map((post: Post) => (
          <Card key={post.id} className="overflow-hidden">
            <CardHeader className="pb-3">
              <div className="flex items-center justify-between">
                <div className="flex items-center space-x-3">
                  <Avatar>
                    <AvatarImage src={post.user.profileImageUrl} />
                    <AvatarFallback>
                      {post.user.firstName?.[0]}{post.user.lastName?.[0]}
                    </AvatarFallback>
                  </Avatar>
                  <div>
                    <p className="font-semibold">{getUserDisplayName(post.user)}</p>
                    <p className="text-sm text-gray-500">
                      {new Date(post.createdAt).toLocaleDateString()}
                    </p>
                  </div>
                </div>
                <Button variant="ghost" size="sm">
                  <MoreHorizontal className="w-4 h-4" />
                </Button>
              </div>
            </CardHeader>

            <CardContent className="pt-0">
              <p className="text-lg mb-4 whitespace-pre-wrap">{post.content}</p>

              {post.imageUrl && (
                <img 
                  src={post.imageUrl} 
                  alt="Post image" 
                  className="w-full rounded-lg mb-4"
                />
              )}

              {/* Actions */}
              <div className="flex items-center justify-between pt-3 border-t">
                <Button
                  variant="ghost"
                  size="sm"
                  onClick={() => handleLike(post)}
                  className={`hover:bg-red-50 hover:text-red-600 ${
                    post.isLiked ? 'text-red-600' : 'text-gray-600'
                  }`}
                >
                  <Heart 
                    className={`w-4 h-4 mr-2 ${post.isLiked ? 'fill-current' : ''}`} 
                  />
                  {post.likesCount}
                </Button>

                <Button
                  variant="ghost"
                  size="sm"
                  onClick={() => setShowComments(showComments === post.id ? null : post.id)}
                  className="hover:bg-blue-50 hover:text-blue-600"
                >
                  <MessageCircle className="w-4 h-4 mr-2" />
                  {post.commentsCount}
                </Button>

                <Button
                  variant="ghost"
                  size="sm"
                  className="hover:bg-green-50 hover:text-green-600"
                >
                  <Share className="w-4 h-4 mr-2" />
                  Share
                </Button>
              </div>

              {/* Comments Section */}
              {showComments === post.id && (
                <div className="mt-4 pt-4 border-t space-y-4">
                  {/* Add Comment */}
                  <div className="flex space-x-2">
                    <Avatar className="w-8 h-8">
                      <AvatarImage src={user?.profileImageUrl} />
                      <AvatarFallback className="text-xs">
                        {user?.firstName?.[0]}{user?.lastName?.[0]}
                      </AvatarFallback>
                    </Avatar>
                    <div className="flex-1 flex">
                      <Input
                        placeholder="Write a comment..."
                        value={newComment}
                        onChange={(e) => setNewComment(e.target.value)}
                        className="rounded-r-none"
                        onKeyPress={(e) => {
                          if (e.key === 'Enter') {
                            handleComment(post.id);
                          }
                        }}
                      />
                      <Button
                        onClick={() => handleComment(post.id)}
                        disabled={!newComment.trim() || commentMutation.isPending}
                        className="rounded-l-none"
                        size="sm"
                      >
                        <Send className="w-4 h-4" />
                      </Button>
                    </div>
                  </div>

                  {/* Comments List */}
                  {comments.map((comment: Comment) => (
                    <div key={comment.id} className="flex space-x-2">
                      <Avatar className="w-8 h-8">
                        <AvatarImage src={comment.user.profileImageUrl} />
                        <AvatarFallback className="text-xs">
                          {comment.user.firstName?.[0]}{comment.user.lastName?.[0]}
                        </AvatarFallback>
                      </Avatar>
                      <div className="flex-1">
                        <div className="bg-gray-100 rounded-lg px-3 py-2">
                          <p className="font-semibold text-sm">
                            {getUserDisplayName(comment.user)}
                          </p>
                          <p className="text-sm">{comment.content}</p>
                        </div>
                        <p className="text-xs text-gray-500 mt-1 ml-3">
                          {new Date(comment.createdAt).toLocaleDateString()}
                        </p>
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </CardContent>
          </Card>
        ))}

        {posts.length === 0 && (
          <Card>
            <CardContent className="text-center py-12">
              <FoxLogo size={60} className="mx-auto mb-4 opacity-50" />
              <h3 className="text-lg font-semibold mb-2">Welcome to EntryFox!</h3>
              <p className="text-gray-600">Start sharing your thoughts with the community.</p>
            </CardContent>
          </Card>
        )}
      </div>
    </div>
  );
}

client/src/pages/success.tsx.content:
import { useLocation } from "wouter";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Check } from "lucide-react";

export default function Success() {
  const [, setLocation] = useLocation();

  const handleContinue = () => {
    const tempUserId = localStorage.getItem('tempUserId');
    const userType = localStorage.getItem('tempUserType');
    localStorage.removeItem('tempUserId');
    localStorage.removeItem('tempUserType');

    // Redirect based on user type
    if (userType === 'vendor') {
      setLocation('/vendor-dashboard');
    } else {
      setLocation('/customer-dashboard');
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-background p-4">
      <div className="w-full max-w-md text-center">
        <Card>
          <CardContent className="pt-6">
            {/* Success Icon */}
            <div className="w-20 h-20 mx-auto mb-6 bg-green-500 rounded-full flex items-center justify-center">
              <Check className="text-white text-2xl" />
            </div>

            {/* Success Message */}
            <h1 className="text-2xl font-bold mb-4">Account Created Successfully!</h1>
            <p className="text-muted-foreground mb-8">
              Welcome to EntryFox! Your account has been created and you're ready to start connecting with your community.
            </p>

            {/* Continue Button */}
            <Button 
              onClick={handleContinue}
              className="w-full bg-black text-white hover:bg-gray-800"
            >
              Continue to Dashboard
            </Button>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

client/src/pages/upload-picture.tsx.content:
import { useState } from "react";
import { useLocation } from "wouter";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Camera } from "lucide-react";
import { useMutation } from "@tanstack/react-query";
import { useToast } from "@/hooks/use-toast";

export default function UploadPicture() {
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const [selectedFile, setSelectedFile] = useState<File | null>(null);
  const [previewUrl, setPreviewUrl] = useState<string | null>(null);

  const uploadMutation = useMutation({
    mutationFn: async (file: File) => {
      const formData = new FormData();
      formData.append('profilePicture', file);
      formData.append('userId', localStorage.getItem('tempUserId') || '');

      const response = await fetch('/api/upload-profile-picture', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.message || 'Upload failed');
      }

      return response.json();
    },
    onSuccess: () => {
      setLocation("/success");
    },
    onError: (error) => {
      toast({
        title: "Upload Failed",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const handleFileChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (file) {
      setSelectedFile(file);
      const url = URL.createObjectURL(file);
      setPreviewUrl(url);
    }
  };

  const handleUpload = () => {
    if (selectedFile) {
      uploadMutation.mutate(selectedFile);
    }
  };

  const handleSkip = () => {
    setLocation("/success");
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-background p-4">
      <div className="w-full max-w-md text-center">
        {/* Header */}
        <div className="mb-8">
          <h1 className="text-2xl font-bold mb-2">Add Profile Picture</h1>
          <p className="text-muted-foreground">Help others recognize you</p>
        </div>

        {/* Upload Area */}
        <Card className="mb-8">
          <CardContent className="pt-6">
            <div className="mb-6">
              <div 
                className="w-32 h-32 mx-auto mb-6 bg-muted rounded-full flex items-center justify-center border-4 border-dashed border-muted-foreground/25 hover:border-accent transition-colors cursor-pointer"
                onClick={() => document.getElementById('fileInput')?.click()}
              >
                {previewUrl ? (
                  <img src={previewUrl} alt="Preview" className="w-full h-full object-cover rounded-full" />
                ) : (
                  <Camera className="text-3xl text-muted-foreground" />
                )}
              </div>
              <input 
                type="file" 
                id="fileInput" 
                accept="image/*" 
                className="hidden" 
                onChange={handleFileChange}
              />

              <Button 
                onClick={() => document.getElementById('fileInput')?.click()}
                variant="outline"
                className="mb-4"
              >
                Choose Photo
              </Button>
            </div>

            {/* Action Buttons */}
            <div className="space-y-3">
              <Button 
                onClick={handleUpload}
                className="w-full bg-black text-white hover:bg-gray-800"
                disabled={!selectedFile || uploadMutation.isPending}
              >
                {uploadMutation.isPending ? "Uploading..." : "Continue"}
              </Button>

              <Button 
                onClick={handleSkip}
                variant="ghost"
                className="w-full text-muted-foreground hover:text-foreground"
              >
                Skip for Now
              </Button>
            </div>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

client/src/pages/verify-otp.tsx.content:
import { Button } from "@/components/ui/button";
import { X, Lock } from "lucide-react";
import { Link } from "wouter";
import { InputOTP, InputOTPGroup, InputOTPSlot } from "@/components/ui/input-otp";
import { useState } from "react";
import { useMutation } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { useLocation } from "wouter";

export default function VerifyOtp() {
  const [otp, setOtp] = useState("");
  const { toast } = useToast();
  const [, setLocation] = useLocation();

  const verifyOtpMutation = useMutation({
    mutationFn: async (code: string) => {
      return await apiRequest("POST", "/api/verify-otp", { code });
    },
    onSuccess: () => {
      setLocation("/reset-password");
    },
    onError: (error) => {
      toast({
        title: "Invalid Code",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const handleOtpComplete = (value: string) => {
    setOtp(value);
    if (value.length === 4) {
      verifyOtpMutation.mutate(value);
    }
  };

  return (
    <div className="min-h-screen bg-white flex items-center justify-center p-4">
      <div className="w-full max-w-sm relative">
        {/* Close Button */}
        <Button
          variant="ghost"
          size="icon"
          className="absolute -top-2 -right-2 w-10 h-10 rounded-full bg-black text-white hover:bg-gray-800"
          asChild
        >
          <Link href="/forgot-password">
            <X className="h-5 w-5" />
          </Link>
        </Button>

        {/* Header */}
        <div className="text-center mb-12">
          <div className="w-16 h-16 rounded-full bg-black flex items-center justify-center mx-auto mb-6">
            <Lock className="h-8 w-8 text-white" />
          </div>
          <h1 className="text-2xl font-bold mb-2">Enter OTP</h1>
        </div>

        {/* OTP Input */}
        <div className="space-y-8">
          <div className="flex justify-center">
            <InputOTP 
              maxLength={4} 
              value={otp} 
              onChange={setOtp}
              onComplete={handleOtpComplete}
            >
              <InputOTPGroup className="gap-2">
                <InputOTPSlot 
                  index={0} 
                  className="w-14 h-14 text-2xl border-2 rounded-xl"
                />
                <InputOTPSlot 
                  index={1} 
                  className="w-14 h-14 text-2xl border-2 rounded-xl"
                />
                <InputOTPSlot 
                  index={2} 
                  className="w-14 h-14 text-2xl border-2 rounded-xl"
                />
                <InputOTPSlot 
                  index={3} 
                  className="w-14 h-14 text-2xl border-2 rounded-xl"
                />
              </InputOTPGroup>
            </InputOTP>
          </div>

          {/* Virtual Keyboard Simulation */}
          <div className="bg-gray-100 rounded-t-3xl p-4">
            <div className="grid grid-cols-3 gap-3 mb-3">
              {[1, 2, 3, 4, 5, 6, 7, 8, 9].map((num) => (
                <Button
                  key={num}
                  variant="ghost"
                  className="h-12 text-xl font-medium hover:bg-gray-200"
                  onClick={() => {
                    if (otp.length < 4) {
                      const newOtp = otp + num.toString();
                      setOtp(newOtp);
                      if (newOtp.length === 4) {
                        handleOtpComplete(newOtp);
                      }
                    }
                  }}
                >
                  {num}
                  <div className="text-xs text-gray-500 mt-1">
                    {num === 2 && "ABC"}
                    {num === 3 && "DEF"}
                    {num === 4 && "GHI"}
                    {num === 5 && "JKL"}
                    {num === 6 && "MNO"}
                    {num === 7 && "PQRS"}
                    {num === 8 && "TUV"}
                    {num === 9 && "WXYZ"}
                  </div>
                </Button>
              ))}
            </div>
            <div className="grid grid-cols-3 gap-3">
              <div></div>
              <Button
                variant="ghost"
                className="h-12 text-xl font-medium hover:bg-gray-200"
                onClick={() => {
                  if (otp.length < 4) {
                    const newOtp = otp + "0";
                    setOtp(newOtp);
                    if (newOtp.length === 4) {
                      handleOtpComplete(newOtp);
                    }
                  }
                }}
              >
                0
              </Button>
              <Button
                variant="ghost"
                className="h-12 hover:bg-gray-200"
                onClick={() => {
                  setOtp(otp.slice(0, -1));
                }}
              >
                ⌫
              </Button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

